def corruption(binary : str, corrupted = 0.25) :
    res = ""
    start = binary.find('1')
    for b in binary[:start//2] :
        if random.random() < corrupted :
            res += '0' if b == '1' else '1'
        else :
            res += b
    res += '1' # previous bits on the 1st byte are altered for sure
    for b in binary[start//2+1:] :
        if random.random() < corrupted :
            res += '0' if b == '1' else '1'
        else :
            res += b
    return res

